import React, { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { addTask } from '../services/api';
import { Task } from '../models/Task';
import { TextField, Button, Box, CircularProgress } from '@mui/material';

interface AddTaskProps {
  onAdd: (title: string) => Promise<void>;
}

const AddTask: React.FC<AddTaskProps> = ({ onAdd }) => {
  const [taskTitle, setTaskTitle] = useState('');
  const queryClient = useQueryClient();

  const mutation = useMutation({
    mutationFn: async (newTask: Omit<Task, 'id'>) => {
      // Call both the API and the onAdd prop
      const result = await addTask(newTask);
      await onAdd(newTask.title); // Call the onAdd callback
      return result;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['tasks'] });
      setTaskTitle('');
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!taskTitle.trim()) return;
    console.log()
    mutation.mutate({ 
      title: taskTitle, 
      completed: false 
    });
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mb: 4 }}>
      <Box display="flex" gap={2} alignItems="center">
        <TextField
          fullWidth
          variant="outlined"
          label="Add new task"
          value={taskTitle}
          onChange={(e) => setTaskTitle(e.target.value)}
          disabled={mutation.isPending}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={!taskTitle.trim() || mutation.isPending}
          sx={{ minWidth: 120, height: 56 }}
        >
          {mutation.isPending ? (
            <CircularProgress size={24} color="inherit" />
          ) : (
            'Add Task'
          )}
        </Button>
      </Box>
      {mutation.isError && (
        <Box color="error.main" mt={1}>
          Error adding task: {mutation.error.message}
        </Box>
      )}
    </Box>
  );
};

export default AddTask;
