import axios from 'axios';
import { Task } from '../models/Task';

// Base configuration
const api = axios.create({
  baseURL: '/api', // Uses proxy in development
  headers: {
    'Content-Type': 'application/json'
  }
});

// Error handling wrapper
const handleRequest = async (request: Promise<any>) => {
  try {
    const response = await request;
    return response.data;
  } catch (error) {
    console.error('API Error:', error);
    throw error;
  }
};

export const getTasks = async (): Promise<Task[]> => {
  return handleRequest(api.get('/tasks')); // Changed from API_PREFIX to '/tasks'
};

export const addTask = async (task: Omit<Task, 'id'>): Promise<Task> => {
  return handleRequest(api.post('/tasks', task));
};

export const updateTask = async (task: Task): Promise<Task> => {
  return handleRequest(api.put(`/tasks/${task.id}`, task));
};

export const deleteTask = async (id: number): Promise<void> => {
  return handleRequest(api.delete(`/tasks/${id}`));
};
