import React from 'react';
import { useTasks } from './hooks/useTasks';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import {
  Box,
  Typography,
  CircularProgress,
  Alert,
  AppBar,
  Toolbar,
  IconButton,
} from '@mui/material';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import { Task } from './models/Task';

interface AppProps {
  toggleTheme: () => void;
  currentMode: 'light' | 'dark';
}

const App: React.FC<AppProps> = ({ toggleTheme, currentMode }) => {
  const { tasks, addTask, toggleTask, deleteTask, isLoading } = useTasks();

  const handleAddTask = async (title: string) => {
    try {
      await addTask({ title, completed: false });
      console.info('Task added successfully from app.tsx');
    } catch (error) {
      console.error('Failed to add task:', error);
    }
  };

  const handleToggleTask = async (task: Task) => {
    try {
      await toggleTask({ ...task, completed: !task.completed });
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  const handleDeleteTask = async (id: number) => {
    try {
      await deleteTask(id);
    } catch (error) {
      console.error('Failed to delete task:', error);
    }
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Todo App
          </Typography>
          <IconButton color="inherit" onClick={toggleTheme}>
            {currentMode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
          </IconButton>
        </Toolbar>
      </AppBar>

      <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
        <AddTask onAdd={handleAddTask} />

        {isLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <CircularProgress />
          </Box>
        ) : tasks?.length === 0 ? (
          <Alert severity="info" sx={{ mt: 2 }}>
            No tasks found. Add your first task!
          </Alert>
        ) : (
          <TaskList
            tasks={tasks || []}
            onToggle={handleToggleTask}
            onDelete={handleDeleteTask}
          />
        )}
      </Box>
    </>
  );
};

export default App;
